import React from 'react';
import {View, StyleSheet} from 'react-native';
import {ToUpperFirst} from '../util'

const PeopleListItem = props => {
    const {people} = props;
    const{title, fisrt, last} = people.name
    return(
        <View style={style.line}>
            <Image style={style.avatar} source={{uri: people.pcture.thumbnail}} />
            <Text style={style.texto} key={fisrt}>
                {
                    `${ToUpperFirst(title)} ${ToUpperFirst(fisrt)} ${ToUpperFirst(last)}`
                }
            </Text>
        </View>
    )
}

const style = StyleSheet.create({
    line: {
        height: 60,
        borderBottomwidth: 1,
        borderBottomColor: '#bbb',
        alignItems: 'center',
        flexDirection: 'row' 
    },
    avatar: {
        aspectRatio: 1,
        marginLeft: 10,
        flex: 1,
        borderRadius: 50
    },
    texto: {
        fontSize: 20,
        paddingLeft: 20,
        flex: 7,
    }
}) 
export default PeopleListItem
